import React, { useState, useEffect } from 'react';
import './number.css';

const Number = ({ targetPlants, targetTrees, targetEvents, targetVillages }) => {
    const [plants, setPlants] = useState(0);
    const [trees, setTrees] = useState(0);
    const [events, setEvents] = useState(0);
    const [villages, setVillages] = useState(0);

    <Number 
    targetPlants={100} 
    targetTrees={50} 
    targetEvents={30} 
    targetVillages={20} 
/>

    useEffect(() => {
        // Increment functions
        const incrementPlants = () => {
            if (plants < targetPlants) {
                setPlants((prev) => prev + 1);
            }
        };

        const incrementTrees = () => {
            if (trees < targetTrees) {
                setTrees((prev) => prev + 1);
            }
        };

        const incrementEvents = () => {
            if (events < targetEvents) {
                setEvents((prev) => prev + 1);
            }
        };

        const incrementVillages = () => {
            if (villages < targetVillages) {
                setVillages((prev) => prev + 1);
            }
        };

        // Set intervals to increment counters
        const plantInterval = setInterval(incrementPlants, 50); // Adjust timing as needed
        const treeInterval = setInterval(incrementTrees, 50);
        const eventInterval = setInterval(incrementEvents, 50);
        const villageInterval = setInterval(incrementVillages, 50);

        // Clear intervals when targets are reached
        return () => {
            clearInterval(plantInterval);
            clearInterval(treeInterval);
            clearInterval(eventInterval);
            clearInterval(villageInterval);
        };
    }, [plants, trees, events, villages, targetPlants, targetTrees, targetEvents, targetVillages]);

    return (
        <div className="number-container">
            <div className="number-container-in">
                <h1>Our Numbers speak for themselves</h1>
                <div className="main-container">
                    <div className="main-container-in">
                        <p>Number of Plants Planted</p>
                        <p>
                            <span>{plants}</span>
                        </p>
                    </div>
                </div>
                <div className="group-container">
                    <div className="group-container-in">
                        <div className="group-box">
                            <p>Number of Trees</p>
                            <p>
                                <span>{trees}</span>
                            </p>
                        </div>
                        <div className="group-box">
                            <p>Number of Events</p>
                            <p>
                                <span>{events}</span>
                            </p>
                        </div>
                        <div className="group-box">
                            <p>Number of Villages</p>
                            <p>
                                <span>{villages}</span>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Number;
