import React, { useCallback } from 'react';
import { tsParticles } from 'tsparticles-engine';
import Particles from 'react-tsparticles';
import Leaf from "../../Assets/Leaf.svg";
import Mail2 from "../../Assets/Mail2.svg";
import './contact.css';

const Contact = () => {
    const [showLeaves, setShowLeaves] = React.useState(false);

    const particlesInit = useCallback(async (engine) => {
        console.log(engine);
        // you can initiate the tsParticles instance (engine) here, adding custom shapes or presets
        await tsParticles.loadFull(engine);
    }, []);

    const handleSubscribe = () => {
        setShowLeaves(true);
        setTimeout(() => {
            setShowLeaves(false);
        }, 5000);
    };

    return (
        <div className="contact">
            <div className="contact-in">
                <div className="contact-in-one">
                    <img src={Mail2} className="NewsletterMail" alt="Newsletter Mail" />
                </div>
                <div className="contact-in-two">
                    <div className="contact-in-two-in">
                        <h1>Subscribe to our newsletter</h1>
                        <p>Get the latest updates on new products and upcoming sales</p>
                        <div className="contact-in-two-in-in">
                            <input type="text" placeholder="Enter your email address" />
                            <button onClick={handleSubscribe}>Subscribe</button>
                        </div>
                    </div>
                </div>
            </div>
            {showLeaves && (
                <Particles
                    id="tsparticles"
                    init={particlesInit}
                    options={{
                        particles: {
                            number: {
                                value: 50,
                            },
                            shape: {
                                type: 'image',
                                image: {
                                    src: {Leaf}, // Replace with your leaf image
                                    width: 100,
                                    height: 100,
                                },
                            },
                            opacity: {
                                value: 0.8,
                            },
                            size: {
                                value: 20,
                                random: true,
                            },
                            move: {
                                direction: "bottom",
                                outMode: "out",
                                speed: 3,
                            },
                        },
                        interactivity: {
                            events: {
                                onClick: {
                                    enable: true,
                                    mode: "push",
                                },
                            },
                        },
                    }}
                />
            )}
        </div>
    );
}

export default Contact;
