import React, { useCallback, useState } from 'react';
import Particles from 'react-tsparticles';
// import { loadFull } from 'tsparticles'; 
import Mail2 from "../../Assets/Mail2.svg";
import './contact.css';

const Contact = () => {
    const [showConfetti, setShowConfetti] = useState(false);

    const particlesInit = useCallback(async (engine) => {
        console.log(engine);
        await loadFull(engine); // Correct usage of loadFull
    }, []);

    const handleSubscribe = () => {
        setShowConfetti(true);
        setTimeout(() => {
            setShowConfetti(false);
        }, 5000); // Confetti display duration
    };

    return (
        <div className="contact">
            <div className="contact-in">
                <div className="contact-in-one">
                    <img src={Mail2} className="NewsletterMail" alt="Newsletter Mail" />
                </div>
                <div className="contact-in-two">
                    <div className="contact-in-two-in">
                        <h1>Subscribe to our newsletter</h1>
                        <p>Get the latest updates on new products and upcoming sales</p>
                        <div className="contact-in-two-in-in">
                            <input type="text" placeholder="Enter your email address" />
                            <button onClick={handleSubscribe}>Subscribe</button>
                        </div>
                    </div>
                </div>
            </div>
            {showConfetti && (
                <Particles
                    id="tsparticles"
                    init={particlesInit}
                    options={{
                        particles: {
                            number: {
                                value: 100,
                            },
                            shape: {
                                type: 'circle', // or 'square', 'triangle', etc.
                            },
                            opacity: {
                                value: 0.6,
                            },
                            size: {
                                value: 10,
                                random: true,
                            },
                            move: {
                                direction: "bottom",
                                outModes: {
                                    default: "out"
                                },
                                speed: 3,
                            },
                        },
                        interactivity: {
                            events: {
                                onClick: {
                                    enable: true,
                                    mode: "push",
                                },
                            },
                        },
                    }}
                />
            )}
        </div>
    );
}

export default Contact;
