import React, { useState } from 'react';
import './number.css';

const Number = () => {
    const [plants, setPlants] = useState(0);
    const [trees, setTrees] = useState(0);
    const [events, setEvents] = useState(0);
    const [villages, setVillages] = useState(0);

    // Increment functions
    const incrementPlants = () => setPlants(plants + 1);
    const incrementTrees = () => setTrees(trees + 1);
    const incrementEvents = () => setEvents(events + 1);
    const incrementVillages = () => setVillages(villages + 1);

    return ( 
        <div className="number-container">
            <div className="number-container-in">
                <h1>Our Numbers speak for themselves</h1>
                <div className="main-container">
                    <div className="main-container-in">
                        <p>Number of Plants Planted</p>
                        <p>
                            <span>{plants}</span>
                        </p>
                        <button onClick={incrementPlants}>Increment Plants</button>
                    </div>
                </div>
                <div className="group-container">
                    <div className="group-container-in">
                        <div className="group-box">
                            <p>Number of Trees</p>
                            <p>
                                <span>{trees}</span>
                            </p>
                            <button onClick={incrementTrees}>Increment Trees</button>
                        </div>
                        <div className="group-box">
                            <p>Number of Events</p>
                            <p>
                                <span>{events}</span>
                            </p>
                            <button onClick={incrementEvents}>Increment Events</button>
                        </div>
                        <div className="group-box">
                            <p>Number of Villages</p>
                            <p>
                                <span>{villages}</span>
                            </p>
                            <button onClick={incrementVillages}>Increment Villages</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Number;
