import React, { useState } from 'react';
import './blog.css';
import { objectives } from '../../Components/objectives'; // Adjust the path accordingly

const Blog_home = () => {
    const [selectedCategory, setSelectedCategory] = useState('All');

    const categories = [
        { id: 'All', name: 'All' },
        { id: 'Afforestation', name: 'Promoting Afforestation' },
        { id: 'Water', name: 'Water Conservation' },
        { id: 'Bio', name: 'Biodiversity Conservation' },
        { id: 'Climate', name: 'Climate Resilience' }
    ];

    const filteredObjectives = selectedCategory === 'All' 
        ? objectives 
        : objectives.filter(objective => objective.title.includes(categories.find(category => category.id === selectedCategory).name));

    return (  
        <div className="blog-home-container">
            <div className="categories">
                {categories.map(category => (
                    <button
                        key={category.id}
                        onClick={() => setSelectedCategory(category.id)}
                        className={selectedCategory === category.id ? 'active' : ''}
                    >
                        {category.name}
                    </button>
                ))}
            </div>
            {filteredObjectives.map((objective) => (
                <div key={objective.id} className="blog-home-container-in">
                    <div className="home-in-one">
                        <h2>{objective.title}</h2>
                        <p>{objective.description}</p>
                        <p>{objective.blog_data}</p>
                        <p>Author: {objective.author.name}</p>
                    </div>
                    <div className="home-in-image">
                        <img src={objective.image} alt={objective.title} />
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Blog_home;
