    return (
        <div className="latest-container" ref={containerRef}>
            <div className="latest-container-in">
                <div className="latest-container-heading">
                    <h1>Latest News and Events</h1>
                </div>
                <div className="latest-container-main">
                    {latest_data.map((item) => (
                        <div key={item.id} className="latest-box">
                            <div className="latest-image">
                                <img src={item.image} alt={item.title} />
                            </div>
                            <div className="latest-info">
                                <h1>{item.title}</h1>
                                <p>{item.description}</p>
                            </div>
                            <div className="latest-learn">
                                <p>Learn More</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Latest;
